  `type_name` varchar(30) DEFAULT NULL COMMENT '博客类别',
  `order_num` int(11) DEFAULT NULL COMMENT '博客排序',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=56 DEFAULT CHARSET=utf8;

